create table ware.ware1(W_ID INTEGER not null auto_increment, W_NAME VARCHAR(10), ADDRESS VARCHAR(20),CONSTRAINT PK1 PRIMARY KEY (W_ID));
create table ware.empl(E_ID INTEGER not null auto_increment, ENAME VARCHAR(10),ADDRESS VARCHAR(15), PHONE INTEGER, passwd varchar(20),W_ID INTEGER, CONSTRAINT PK2  PRIMARY KEY (E_ID), CONSTRAINT FK1 FOREIGN KEY (W_ID)  REFERENCES WARE1(W_ID) on delete cascade);
CREATE TABLE ware.prod(P_ID INTEGER not null auto_increment,PNAME VARCHAR(10), Dofmanf DATE , QUANTITY INTEGER, W_ID INTEGER , CONSTRAINT PK3 PRIMARY KEY (P_ID), CONSTRAINT FK2 FOREIGN KEY (W_ID) REFERENCES ware1(W_ID)on delete cascade);
CREATE TABLE ware.CUST ( C_ID INTEGER not null auto_increment, CNAME VARCHAR(10),  ADDRESS VARCHAR(20), PHONE INTEGER,passwd varchar(20), E_ID INTEGER, CONSTRAINT PK4 PRIMARY KEY(C_ID), CONSTRAINT FK3 FOREIGN KEY (E_ID) REFERENCES EMPL(E_ID)on delete cascade);
CREATE TABLE ware.SHIP(SHIP_ID INTEGER not null auto_increment,shipname varchar(20), DofSHIp DATE, QUANTITY INTEGER, P_ID INTEGER, C_ID INTEGER,statu varchar(20),E_ID INTEGER, CONSTRAINT PK5 PRIMARY KEY(SHIP_ID), CONSTRAINT FK4 FOREIGN KEY(P_ID) REFERENCES PROD(P_ID) on delete cascade, CONSTRAINT FK5 FOREIGN KEY(C_ID) REFERENCES  CUST(C_ID) on delete cascade,CONSTRAINT FK11 FOREIGN KEY(E_ID) REFERENCES empl(E_id) on delete cascade);
CREATE TABLE ware.receipt (dateofship DATE, SHIP_ID INTEGER,totalqunt INTEGER,  CONSTRAINT PK6 PRIMARY KEY (SHIP_ID), CONSTRAINT FK6 FOREIGN KEY (SHIP_ID) REFERENCES SHIP(SHIP_ID)on delete cascade);
CREATE TABLE ware.LOCATION (W_ID INTEGER, P_ID INTEGER, LOCATION VARCHAR(10), CONSTRAINT PK7 PRIMARY KEY(W_ID,P_ID), CONSTRAINT FK7 FOREIGN KEY (W_ID) REFERENCES WARE1(W_ID) on delete cascade,CONSTRAINT FK8 FOREIGN KEY (P_ID) REFERENCES PROD(P_ID) on delete cascade);
CREATE TABLE ware.CONTAINED_IN( P_ID INTEGER, SHIP_ID INTEGER, CONSTRAINT PK8 PRIMARY KEY(P_ID,SHIP_ID), CONSTRAINT FK9 FOREIGN KEY (P_ID) REFERENCES PROD(P_ID) on delete cascade, CONSTRAINT FK10 FOREIGN KEY (SHIP_ID) REFERENCES SHIP(SHIP_ID)on delete cascade);
